import {useDispatch} from 'react-redux';
export const ADD_SYMBOL = "ADD_SYMBOL";

const dispatch = useDispatch();
export const addSymbol = (setSymbol) => {
    const action = {
        type: ADD_SYMBOL,
        payload: setSymbol,
        increment: 1
    }
    dispatch(action);
}
