import {ADD_SYMBOL} from '../actions';



const game = (state, action) => {
    switch(action.type) {
        case "ADD_SYMBOL":
            return {
                squares: [action.payload],
                moves: action.increment
            }
        case "SETUP":
            return {
                ...state,
                squares: action.squares,
                xIsNext: true,
                moves: action.moves
            }
        default:
            return state;
    }
}

export default game;
